choose.when:
SELECT B.* FROM(
	SELECT  A.*, ROWNUM() as rn FROM(
		SELECT * FROM community ORDER BY checkadmin DESC, num DESC 
	)A
)B WHERE rn BETWEEN #{begin} and #{end}

SELECT B.* FROM(
	SELECT  A.*, ROWNUM() as rn FROM(
		SELECT * FROM community
			WHERE id LIKE CONCAT('%',#{search},'%')
		 ORDER BY num DESC 
	)A
)B WHERE rn BETWEEN #{begin} and #{end}

SELECT B.* FROM(
	SELECT  A.*, ROWNUM() as rn FROM(
		SELECT * FROM community
			WHERE title LIKE CONCAT('%',#{search},'%')
		 ORDER BY num DESC 
	)A
)B WHERE rn BETWEEN #{begin} and #{end}

SELECT B.* FROM(
	SELECT  A.*, ROWNUM() as rn FROM(
		SELECT * FROM community
			WHERE content LIKE CONCAT('%',#{search},'%')
		 ORDER BY num DESC 
	)A
)B WHERE rn BETWEEN #{begin} and #{end}

SELECT B.* FROM(
	SELECT  A.*, ROWNUM() as rn FROM(
		SELECT * FROM community
			WHERE id LIKE CONCAT('%',#{search},'%') or
			 title LIKE CONCAT('%',#{search},'%') or
			 num LIKE CONCAT('%',#{search},'%') or
			 content LIKE CONCAT('%',#{search},'%')
		 ORDER BY num DESC 
	)A
)B WHERE rn BETWEEN #{begin} and #{end}

SELECT B.* FROM(
	SELECT  A.*, ROWNUM() as rn FROM(
		SELECT * FROM community
			WHERE num LIKE CONCAT('%',#{search},'%')
		 ORDER BY num DESC 
	)A
)B WHERE rn BETWEEN #{begin} and #{end}

mapper.insert:
       
	    INSERT INTO community_comment (writeNo, reWriter, deleted, reContent, reDate,REPARENT,REDEPTH) VALUES (#{writeNo}, #{reWriter}, 'N', #{reContent}, #{reDate},
	    
	    	'없음', 0
	    	#{reparent}, #{redepth}
	    
	    )

mapper.select:
/* community : 커뮤니티 테이블 조회 */
SELECT B.* FROM(

				SELECT  A.*, ROWNUM() as rn FROM(
					SELECT * FROM community
						WHERE id =#{id}
					 ORDER BY num DESC 
				)A
			)B WHERE rn BETWEEN #{begin} and #{end}

mapper.update:
UPDATE community b
	set b.commentCount=(SELECT count(writeNo) FROM community_comment WHERE writeNo=#{writeNo} and deleted='N')
WHERE b.num=#{writeNo}

UPDATE community b
	set b.commentCount=(SELECT count(writeNo) FROM community_comment WHERE writeNo=#{writeNo} and deleted='N')
WHERE b.num=#{writeNo}
